package org.in;

public class SwapTwonumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		System.out.println("before swapping numbers: "+" "+ a+" "+ b);
		int c;
		c=b;
		b=a;
		a=c;
		System.out.println("after swapping numbers: "+ " "+a+" "+ b);
		
		//another method
		
	}

}
